<style lang="scss" module="menu">
.extend {
  display: flex;
  align-items: center;
  flex-direction: column;
  width: 200px;
  height: 100vh;
  background-color: $m-800;
  padding: 40px 16px 20px 16px;
}

.reduction {
  display: flex;
  align-items: center;
  flex-direction: column;
  width: 60px;
  height: 100vh;
  background-color: $m-900;
  padding: 40px 8px;
}
</style>

<style lang="scss" module="logo">
.white {
  width: 157px;
  height: 36px;
  margin-bottom: 88px;
  border: none;
  outline: none;
}

.none {
  width: 60px;
  height: 46px;
  margin-bottom: 88px;
}
</style>

<style lang="scss" module="nav">
.main {
  width: 100%;
  height: 100%;
}
.items {
  display: flex;
  align-items: center;
  width: 100%;
  height: 48px;
  border-radius: 3px;
  color: $m-300;
  cursor: pointer;
  margin-bottom: 8px;
}
.select_color {
  color: #ffffff;
}

.select_background {
  background-color: rgba(255, 255, 255, 0.1);
  padding-top: 4px;
}

.icon {
  padding: 0 10px;
}

.text {
  font-size: 14px;
  font-weight: 500;
  line-height: 40px;
  width: calc(100% - 80px);
}

.badge {
  width: 25px;
  height: 16px;
  font-size: 12px;
  font-weight: 500;
  line-height: 16px;
  border-radius: 8px;
  background-color: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
  color: $m-900;
}
</style>

<style lang="scss" module="myinfo">
.layout_extend {
  position: relative;
  width: 100%;
  display: flex;
  align-items: center;

  div {
    width: 100%;
  }
}

.layout {
  position: relative;
  width: 100%;
  display: flex;
  align-items: center;
  padding-left: 5px;
}

.name {
  font-size: 14px;
  font-weight: 600;
  color: $n-20;
}

.team {
  font-size: 11px;
  font-weight: 400;
  color: $m-300;
  padding: 5px 0;
}

.icon {
  cursor: pointer;
}

.modal_extend {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  width: 156px;
  height: 80px;
  position: absolute;
  left: 190px;
  top: -40px;
  border-radius: 3px;
  background-color: #ffffff;
  z-index: 1;
  box-shadow: 0px 3px 5px rgba(9, 30, 66, 0.2),
    0px 0px 1px rgba(9, 30, 66, 0.31);
}

.modal {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  position: absolute;
  right: -170px;
  top: -20px;
  width: 156px;
  height: 80px;
  border-radius: 3px;
  background-color: #ffffff;
  box-shadow: 0px 3px 5px rgba(9, 30, 66, 0.2),
    0px 0px 1px rgba(9, 30, 66, 0.31);
}

.modal_btn {
  outline: none;
  width: 100%;
  height: 32px;
  color: $m-800;
  font-weight: 400;
  font-size: 14px;
  border: none;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  background-color: transparent;
  cursor: pointer;
  padding: 0 10px;

  &:hover {
    background-color: $n-20;
  }

  i {
    padding: 0 10px;
  }
}
</style>
