import { ref, computed } from "vue";
import DocumentImg1 from "@/assets/img/type01.png";
import DocumentImg2 from "@/assets/img/type02.png";
import DocumentImg3 from "@/assets/img/type03.png";
import UploadCloud from "@/assets/img/upload_cloud.png";
import { useWorkStore } from "@/store";
import { storeToRefs } from "pinia";

interface Props {
  closeCallback: Function;
  upload: Function;
}

const DOCUMENTS = [
  "선택한 문서 종류",
  "해외투자신고 서류",
  "무역금융 서류",
  "일반 서류",
];

const REGEX = /(png|jpg|tiff|tif|pdf)/g;

const props = defineProps<Props>();

const workStore = useWorkStore();
const { files } = storeToRefs(workStore);

const currentStep = ref<number>(2);
const documentType = ref<number>(0);
const dropZone = ref<HTMLDivElement | null>(null);
const uploadCnt = ref<number>(0);

const step_1_color = computed(() => {
  return currentStep.value > 1 ? "#5EAE91" : "#ffffff";
});
const step_2_color = computed(() => {
  return currentStep.value > 2
    ? "#5EAE91"
    : currentStep.value === 2
    ? "#ffffff"
    : "rgba(255,255,255,0.5)";
});
const step_3_color = computed(() => {
  return currentStep.value > 3
    ? "#5EAE91"
    : currentStep.value === 3
    ? "#ffffff"
    : "rgba(255,255,255,0.5)";
});
const drop_zone_height = computed(() =>
  files.value.length > 0 ? "40%" : "100%"
);
const document = computed(() => DOCUMENTS[documentType.value]);

const onDocumentSelect = (n: number) => {
  documentType.value = n;
};

const onUploadBtn = () => {
  props.upload();
};

const mousemove = (e: MouseEvent) => {
  if (!dropZone.value) {
    return;
  }
  if (e.buttons !== 1) {
    dropZone.value.classList.remove("on");
    return;
  }
  dropZone.value.classList.add("on");
};

const mouseleave = (e: MouseEvent) => {
  if (!dropZone.value) {
    return;
  }
  dropZone.value.classList.remove("on");
};

const onClose = (e: MouseEvent) => {
  e.preventDefault();
  e.stopPropagation();
  const el = e.target as HTMLElement;
  if (!el) {
    return;
  }
  const contains = el.classList.contains("modal");
  if (!contains) {
    return;
  }
  props.closeCallback();
};

const onNext = () => {
  currentStep.value = currentStep.value + 1;
};

const onPrev = () => {
  currentStep.value = currentStep.value - 1;
};

const onDropFiles = (e: DragEvent) => {
  e.preventDefault();
  const dt = e.dataTransfer as DataTransfer;
  if (!dt) {
    return;
  }
  for (let i = 0; i < dt.items.length; i++) {
    const f = dt.items[i].getAsFile();
    if (!f || !REGEX.test(f.type)) {
      return;
    }

    const item = {
      id: `${f.lastModified}${Date.now}`,
      complete: false,
      type: f.type.split("/")[1],
      file: f,
    };
    files.value.push(item);
  }
};

const onDragover = (e: DragEvent) => {
  e.preventDefault();
};

const onDeleteFile = (id: string) => {
  files.value = files.value.filter((file) => file.id !== id);
};

const onUploadFiles = () => {
  currentStep.value = currentStep.value + 1;

  let c = 1;
  for (const item of files.value) {
    setTimeout(() => {
      item.complete = true;
      uploadCnt.value = uploadCnt.value + 1;
      if (files.value.length === uploadCnt.value) {
        currentStep.value = currentStep.value + 1;
      }
    }, 1000 * c);
    c++;
  }
};
</script>

